import { useState } from 'react' //共享状态的 useState 方法 rect hooks

export default function useStats(url) {
  // console.log(url);
  // from input 双向绑定新写法 
  const [stats, setStats] = useState()
  // 加载 ajax loading
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState()
  // 封装 state 的处理
  return {
    stats,
    loading,
    error
  }
}
